# Generate SQL
sql_content = "-- Comprehensive product catalog for grocery store\n\n"

uuid_counter = 1
for category, products in products_data.items():
    sql_content += f"-- {category} Products\n"
    for name, description, price, stock, image_url in products:
        product_uuid = f"550e8400-e29b-41d4-a716-4466554400{uuid_counter:02d}"
        sql_content += f"INSERT INTO products (id, name, description, price, category, stock, image_url, created_at, updated_at) VALUES ('{product_uuid}', '{name}', '{description}', {price}, '{category}', {stock}, '{image_url}', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);\n"
        uuid_counter += 1
    sql_content += "\n"

# Save to file
with open('data.sql', 'w', encoding='utf-8') as f:
    f.write(sql_content)

print(f"Generated {uuid_counter - 1} products across {len(products_data)} categories")
print("File saved as: data.sql")
